import { apiCall } from "../../utils/apiCall"

// post methods 

export const handleRegister = (e,formData) => ( apiCall("POST","USER" ,  "/user/register", undefined,  e , formData ))

export const handleLogin =  (e,formData) => ( apiCall("POST","USER" ,"/user/login",undefined, e , formData )) 

export const forgotpassword =  (e,formData) => (apiCall("POST", "USER" , "/user/forgot/password",undefined, e , formData )) 

export const OtpVerify = (e , formData , token)=>(apiCall("POST" ,"USER" , `/user/otp/verify/${token}` , undefined , e , formData))


//put
export const editUser = (e , formData ,userId) =>(apiCall("PUT" , "USER" , "/user/edit" , userId ,e , formData ))

export const uploadProfile = (e , formData , userId) =>(apiCall("PUT" , "USER" , "/user/upload/profile" , userId , e , formData))


// delete method 
export const deleteUser =  (userId) => (apiCall( "DELETE" , "USER" , `user/delete` , userId)) 

// get method 
export const verifyUser =  (token)=>(apiCall( "GET" , "USER" , `verify/${token}` ))
