export const Context = createContext();

const Store = () => {
  //  const btnName = "Something"

  const [store, setStore] = React.useState({
    btnName: "Something",
    darkMode: false,
    username : "John Doe",
  });




  const handleRegsister = async (e , form) => {
    e.preventDefault();

    try {


      const res = await axios.post(`http://localhost:5157/api/user/register`, form);

      console.log(res.data);

        if (res.status === 200) { toast.success("User registered successfully")}


      // form sanitization
      setForm({
        username : "",
        email: "",
        password: "",    
      });
    } catch (error) {

        if([400, 401, 403, 404 , 500].includes(error.response.status) ) {
            toast.error(error.response.data.message)
          }

          console.log(error);
    }
  };





  return (
    <Context.Provider value={store}>
      <App />
    </Context.Provider>
  );
};

export default Store;
