export const apiCall = (url , e , formData , token) => async (action) => {
    try {
      e.preventDefault();

      let res; 

      action({ type: "API_REQUEST" }); // state.loading :true


      if(formData){
         res = await axiosInstance.post(`${url}`, formData)
      }
      else{
         res = await axiosInstance.get(`${url}/${token}`);
      }

      if (res.status === 200) {

        action({
          type: "API_SUCCESS",
          payload: res.data.payload,
          message: res.data.message,
          username : res.data.username,
          email : res.data.email
        });

        return true

      }else{
        return false
      }
    } catch (err) {


      console.log(err);
      action({
        type: "API_FAILURE",
        error: err,
        message: err.message,
      });

      return false
    }
  };
