import { axiosInstance } from "./axiosInstance";

export const apiCall =
  (requsetType, url, paramId , e, formData) => async (action) => {
    try {
      if (e !== undefined) {
        e.preventDefault();
      }

      let res;

      action({ type: "API_REQUEST" }); // state.loading :true

      if (requsetType === "post") {

        res = await axiosInstance.post(`${url}`, formData);

      } else if (requsetType === "put" && paramId !== undefined) {

        res = await axiosInstance.put(`${url}/${paramId}`, formData);

      } else if (requsetType === "get") {

        if (paramId !== undefined) {
          res = await axiosInstance.get(`${url}/${paramId}`);
        } else {
          res = await axiosInstance.get(url);
        }

      } else if (requsetType === "delete") {

        if (paramId !== undefined) {
          res = await axiosInstance.delete(`${url}/${paramId}`);
        } else {
          res = await axiosInstance.delete(url);
        }

      }

      if (res.status === 200) {
        action({
          type: "API_SUCCESS",
          payload: res.data.payload,
          message: res.data.message,
          username: res.data.username,
          email: res.data.email,
        });

        return true;
      } else {
        return false;
      }
    } catch (err) {
      console.error(err);

        console.log(err.status)
        action({
          type: "API_FAILURE",
          error: err,
          // message: err.response.data.message,
          message: "err.response.data.message",
        });




      return false;
    }
  };
