//put
export const editUser = (e , formData ,userId) =>(userApiCall("put" , "/user/edit" , userId ,e , formData ))

export const uploadProfile = (e , formData , userId) =>(userApiCall("put" , "/user/upload/profile" , userId , e , formData))


// delete method 
export const deleteUser =  (userId) => (userApiCall( "delete" , `/user/delete` , userId)) 

// get method 
